import React, { useState } from 'react'

export const ToDo = (props) => {
    const [ todo, setToDo ] = useState('')
    return(
        <div>
            <input type = 'text' value = {todo} onChange = {e => setToDo(e.target.value)} placeholder = 'Add your task'></input>
            <button onClick = { e => props.setToDos(props.todos.push(todo))}>Submit</button>
        </div>
    )
}
